import{_ as a,c as o,o as r,a3 as i}from"./chunks/framework.p1dVAvtz.js";const t="/Doc-Flows/assets/param.CnlBF9uE.webp",l="/Doc-Flows/assets/encabezado.tO9OGJEP.webp",g=JSON.parse('{"title":"📘 Manual de Usuario - Reporte Detalle de Cisterna","description":"","frontmatter":{},"headers":[],"relativePath":"reportes/CisternaUsuario.md","filePath":"reportes/CisternaUsuario.md","lastUpdated":1747955015000}'),s={name:"reportes/CisternaUsuario.md"};function n(d,e,c,u,m,p){return r(),o("div",null,e[0]||(e[0]=[i('<h1 id="📘-manual-de-usuario-reporte-detalle-de-cisterna" tabindex="-1">📘 Manual de Usuario - Reporte Detalle de Cisterna <a class="header-anchor" href="#📘-manual-de-usuario-reporte-detalle-de-cisterna" aria-label="Permalink to &quot;📘 Manual de Usuario - Reporte Detalle de Cisterna&quot;">​</a></h1><h2 id="🧾-descripcion-del-reporte" tabindex="-1">🧾 Descripción del Reporte <a class="header-anchor" href="#🧾-descripcion-del-reporte" aria-label="Permalink to &quot;🧾 Descripción del Reporte&quot;">​</a></h2><p>El reporte <strong>&quot;Detalle de Cisterna&quot;</strong> permite visualizar el historial completo de movimientos de stock de un artículo específico en una cisterna determinada, mostrando el <strong>saldo acumulado en tiempo real</strong>.</p><h2 id="📊-informacion-que-muestra-el-reporte" tabindex="-1">📊 Información que Muestra el Reporte <a class="header-anchor" href="#📊-informacion-que-muestra-el-reporte" aria-label="Permalink to &quot;📊 Información que Muestra el Reporte&quot;">​</a></h2><h2 id="⚙️-parametros-del-reporte" tabindex="-1">⚙️ Parámetros del Reporte <a class="header-anchor" href="#⚙️-parametros-del-reporte" aria-label="Permalink to &quot;⚙️ Parámetros del Reporte&quot;">​</a></h2><p>Para generar el reporte correctamente, se deben completar los siguientes parámetros:</p><h3 id="🔐-parametros-obligatorios" tabindex="-1">🔐 Parámetros Obligatorios <a class="header-anchor" href="#🔐-parametros-obligatorios" aria-label="Permalink to &quot;🔐 Parámetros Obligatorios&quot;">​</a></h3><ul><li>1️⃣ <strong><code>Combustible</code></strong>: ID del artículo a consultar</li></ul><h3 id="🧩-parametros-opcionales" tabindex="-1">🧩 Parámetros Opcionales <a class="header-anchor" href="#🧩-parametros-opcionales" aria-label="Permalink to &quot;🧩 Parámetros Opcionales&quot;">​</a></h3><ul><li>2️⃣ <strong><code>fechaDesde</code></strong>: Fecha de inicio del período</li><li>2️⃣ <strong><code>fechaHasta</code></strong>: Fecha final del período</li><li>3️⃣ <strong><code>Ordenar</code></strong>: Ascendente o Descendente (selección por desplegable)</li><li>4️⃣ <strong><code>Cantidad de filas</code></strong>: Número máximo de registros a mostrar (por defecto: 50).<br> 🔸 <em>No tiene un límite máximo o mínimo fijo.</em><br> 🔸 <em>Si se aplica un filtro por fecha, se mostrarán todos los movimientos correspondientes al rango seleccionado.</em></li></ul><p><img src="'+t+'" alt="parametroarticuloid"></p><h3 id="🧱-encabezado-del-reporte" tabindex="-1">🧱 Encabezado del Reporte <a class="header-anchor" href="#🧱-encabezado-del-reporte" aria-label="Permalink to &quot;🧱 Encabezado del Reporte&quot;">​</a></h3><ul><li>🛢️ <strong>Nombre de la cisterna</strong>: Muestra <code>&quot;CISTERNA CON [nombre del artículo]&quot;</code></li><li>📦 <strong>Total disponible</strong>: Cantidad total disponible del artículo en la cisterna</li></ul><p><img src="'+l+'" alt="encabezado"></p><h3 id="📋-columnas-del-detalle" tabindex="-1">📋 Columnas del Detalle <a class="header-anchor" href="#📋-columnas-del-detalle" aria-label="Permalink to &quot;📋 Columnas del Detalle&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Columna</th><th>Descripción</th></tr></thead><tbody><tr><td>🆔 <strong>Referencia</strong></td><td>Código o referencia del movimiento</td></tr><tr><td>📅 <strong>Fecha</strong></td><td>Fecha y hora del movimiento (formato <code>YYYY-MM-DD HH:MM</code>)</td></tr><tr><td>🔄 <strong>Movimiento</strong></td><td>Tipo de operación realizada</td></tr><tr><td>🔢 <strong>Cantidad</strong></td><td>Cantidad del movimiento</td></tr><tr><td>📈 <strong>Saldo</strong></td><td>Saldo acumulado después del movimiento</td></tr></tbody></table><h2 id="🧭-tipos-de-movimientos" tabindex="-1">🧭 Tipos de Movimientos <a class="header-anchor" href="#🧭-tipos-de-movimientos" aria-label="Permalink to &quot;🧭 Tipos de Movimientos&quot;">​</a></h2><p>El reporte clasifica los movimientos en tres categorías con colores distintivos:</p><h3 id="🔴-carga-rojo" tabindex="-1">🔴 <strong>Carga (Rojo)</strong> <a class="header-anchor" href="#🔴-carga-rojo" aria-label="Permalink to &quot;🔴 **Carga (Rojo)**&quot;">​</a></h3><ul><li>🎨 <strong>Fondo</strong>: Rojo claro <code>#FFCDD2</code></li><li>🖍️ <strong>Texto</strong>: Rojo oscuro <code>#C62828</code></li><li>🧾 <strong>Descripción</strong>: Movimientos que <strong>reducen el stock</strong> de la cisterna</li></ul><h3 id="🟢-descarga-verde" tabindex="-1">🟢 <strong>Descarga (Verde)</strong> <a class="header-anchor" href="#🟢-descarga-verde" aria-label="Permalink to &quot;🟢 **Descarga (Verde)**&quot;">​</a></h3><ul><li>🎨 <strong>Fondo</strong>: Verde claro <code>#C8E6C9</code></li><li>🖍️ <strong>Texto</strong>: Verde oscuro <code>#2E7D32</code></li><li>🧾 <strong>Descripción</strong>: Movimientos que <strong>incrementan el stock</strong></li></ul><h3 id="🟡-ajuste-amarillo" tabindex="-1">🟡 <strong>Ajuste (Amarillo)</strong> <a class="header-anchor" href="#🟡-ajuste-amarillo" aria-label="Permalink to &quot;🟡 **Ajuste (Amarillo)**&quot;">​</a></h3><ul><li>🎨 <strong>Fondo</strong>: Amarillo claro <code>#FFE082</code></li><li>🖍️ <strong>Texto</strong>: Naranja oscuro <code>#FF8F00</code></li><li>🧾 <strong>Descripción</strong>: Ajustes <strong>manuales de inventario</strong></li></ul><h2 id="🧰-funcionalidades-especiales" tabindex="-1">🧰 Funcionalidades Especiales <a class="header-anchor" href="#🧰-funcionalidades-especiales" aria-label="Permalink to &quot;🧰 Funcionalidades Especiales&quot;">​</a></h2><h3 id="📆-filtrado-por-fechas" tabindex="-1">📆 Filtrado por Fechas <a class="header-anchor" href="#📆-filtrado-por-fechas" aria-label="Permalink to &quot;📆 Filtrado por Fechas&quot;">​</a></h3><ul><li>Sin fechas: muestra los <strong>últimos 6 meses</strong></li><li>Una sola fecha: muestra los <strong>movimientos de ese día</strong></li><li>Rango: muestra <strong>todos los movimientos en ese período</strong></li></ul><h3 id="📌-limite-de-registros" tabindex="-1">📌 Límite de Registros <a class="header-anchor" href="#📌-limite-de-registros" aria-label="Permalink to &quot;📌 Límite de Registros&quot;">​</a></h3><ul><li>📅 <strong>Consultas del día actual</strong>: máximo <strong>50 registros</strong> (o el límite indicado)</li><li>📜 <strong>Consultas históricas</strong>: <strong>sin límite</strong></li></ul><h3 id="📐-calculo-de-saldos" tabindex="-1">📐 Cálculo de Saldos <a class="header-anchor" href="#📐-calculo-de-saldos" aria-label="Permalink to &quot;📐 Cálculo de Saldos&quot;">​</a></h3><ul><li>Calcula automáticamente el <strong>saldo inicial antes del período consultado</strong></li><li>Muestra el <strong>saldo acumulado progresivo</strong></li><li>Actualiza saldos en tiempo real según el tipo de movimiento</li></ul><h2 id="📖-interpretacion-de-los-datos" tabindex="-1">📖 Interpretación de los Datos <a class="header-anchor" href="#📖-interpretacion-de-los-datos" aria-label="Permalink to &quot;📖 Interpretación de los Datos&quot;">​</a></h2><h3 id="📊-lectura-del-saldo-acumulado" tabindex="-1">📊 Lectura del Saldo Acumulado <a class="header-anchor" href="#📊-lectura-del-saldo-acumulado" aria-label="Permalink to &quot;📊 Lectura del Saldo Acumulado&quot;">​</a></h3><ul><li>➕ <strong>Saldo positivo</strong>: producto disponible</li><li>➖ <strong>Saldo negativo</strong>: déficit o sobregiro</li><li>0️⃣ <strong>Saldo cero</strong>: cisterna vacía</li></ul><h3 id="🧠-analisis-de-movimientos" tabindex="-1">🧠 Análisis de Movimientos <a class="header-anchor" href="#🧠-analisis-de-movimientos" aria-label="Permalink to &quot;🧠 Análisis de Movimientos&quot;">​</a></h3><ul><li>Ordenados cronológicamente</li><li>Cada fila muestra el impacto en el saldo</li><li>Referencia útil para rastrear el origen del movimiento</li></ul><h2 id="✅-casos-de-uso-comunes" tabindex="-1">✅ Casos de Uso Comunes <a class="header-anchor" href="#✅-casos-de-uso-comunes" aria-label="Permalink to &quot;✅ Casos de Uso Comunes&quot;">​</a></h2><h3 id="🧮-control-de-inventario" tabindex="-1">🧮 Control de Inventario <a class="header-anchor" href="#🧮-control-de-inventario" aria-label="Permalink to &quot;🧮 Control de Inventario&quot;">​</a></h3><ul><li>Verificar stock actual</li><li>Rastrear movimientos irregulares</li><li>Analizar patrones históricos de consumo</li></ul><h3 id="🔍-auditoria" tabindex="-1">🔍 Auditoría <a class="header-anchor" href="#🔍-auditoria" aria-label="Permalink to &quot;🔍 Auditoría&quot;">​</a></h3><ul><li>Validar precisión de registros</li><li>Investigar discrepancias</li><li>Documentar historial completo</li></ul><h3 id="📈-planificacion" tabindex="-1">📈 Planificación <a class="header-anchor" href="#📈-planificacion" aria-label="Permalink to &quot;📈 Planificación&quot;">​</a></h3><ul><li>Proyección de necesidades</li><li>Identificación de tendencias</li><li>Optimización de niveles de stock</li></ul><h2 id="📌-notas-importantes" tabindex="-1">📌 Notas Importantes <a class="header-anchor" href="#📌-notas-importantes" aria-label="Permalink to &quot;📌 Notas Importantes&quot;">​</a></h2><ul><li>Datos en tiempo real desde la base actual</li><li>Ajustes manuales claramente diferenciados</li><li>Fechas en formato 24 horas</li><li>Números con decimales cuando corresponda</li><li>Colores alternos por fila para mejor lectura</li></ul><h2 id="🛠️-solucion-de-problemas-comunes" tabindex="-1">🛠️ Solución de Problemas Comunes <a class="header-anchor" href="#🛠️-solucion-de-problemas-comunes" aria-label="Permalink to &quot;🛠️ Solución de Problemas Comunes&quot;">​</a></h2><h3 id="❌-no-aparecen-datos" tabindex="-1">❌ No aparecen datos <a class="header-anchor" href="#❌-no-aparecen-datos" aria-label="Permalink to &quot;❌ No aparecen datos&quot;">​</a></h3><ul><li>Verificar ID del artículo</li><li>Confirmar cisterna correcta</li><li>Asegurar existencia de movimientos en el período</li></ul><h3 id="⚠️-saldos-incorrectos" tabindex="-1">⚠️ Saldos incorrectos <a class="header-anchor" href="#⚠️-saldos-incorrectos" aria-label="Permalink to &quot;⚠️ Saldos incorrectos&quot;">​</a></h3><ul><li>Saldos se calculan automáticamente</li><li>Revisar integridad de datos históricos</li></ul><h3 id="🐢-rendimiento-lento" tabindex="-1">🐢 Rendimiento lento <a class="header-anchor" href="#🐢-rendimiento-lento" aria-label="Permalink to &quot;🐢 Rendimiento lento&quot;">​</a></h3><ul><li>Filtrar fechas más específicas</li><li>Usar límite de registros para pruebas</li></ul><h2 id="❓-preguntas-frecuentes-faq" tabindex="-1">❓ Preguntas Frecuentes (FAQ) <a class="header-anchor" href="#❓-preguntas-frecuentes-faq" aria-label="Permalink to &quot;❓ Preguntas Frecuentes (FAQ)&quot;">​</a></h2><h3 id="_1-¿por-que-no-veo-movimientos-en-mi-consulta" tabindex="-1">1. ¿Por qué no veo movimientos en mi consulta? <a class="header-anchor" href="#_1-¿por-que-no-veo-movimientos-en-mi-consulta" aria-label="Permalink to &quot;1. ¿Por qué no veo movimientos en mi consulta?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Las causas más comunes son:</p><ul><li>❌ El ID del artículo no existe o es incorrecto</li><li>📆 No hay movimientos en el rango de fechas seleccionado</li><li>🛢️ El depósito/cisterna especificado no contiene el artículo</li><li>⏳ Los movimientos fueron realizados fuera del período de 6 meses (si no especificó fechas)</li></ul><h3 id="_2-¿que-significa-cuando-aparece-n-a-en-la-columna-referencia" tabindex="-1">2. ¿Qué significa cuando aparece &quot;N/A&quot; en la columna Referencia? <a class="header-anchor" href="#_2-¿que-significa-cuando-aparece-n-a-en-la-columna-referencia" aria-label="Permalink to &quot;2. ¿Qué significa cuando aparece &quot;N/A&quot; en la columna Referencia?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> &quot;N/A&quot; indica que el movimiento no tiene una referencia de texto asociada. Esto puede ocurrir con:</p><ul><li>🔁 Movimientos automáticos del sistema</li><li>🧾 Ajustes masivos de inventario</li><li>📂 Movimientos antiguos sin documentación completa</li></ul><h3 id="_3-¿por-que-el-saldo-mostrado-no-coincide-con-mi-sistema-de-inventario" tabindex="-1">3. ¿Por qué el saldo mostrado no coincide con mi sistema de inventario? <a class="header-anchor" href="#_3-¿por-que-el-saldo-mostrado-no-coincide-con-mi-sistema-de-inventario" aria-label="Permalink to &quot;3. ¿Por qué el saldo mostrado no coincide con mi sistema de inventario?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Posibles causas:</p><ul><li>⏱️ El reporte muestra el saldo histórico en el momento específico de cada movimiento</li><li>📉 Pueden existir movimientos posteriores que no están incluidos en el filtro de fechas</li><li>🧭 Verificar que esté consultando el depósito correcto</li><li>📚 El cálculo considera todos los movimientos históricos, incluso anteriores al período consultado</li></ul><h3 id="_4-¿como-interpretar-las-cantidades-negativas-en-el-saldo" tabindex="-1">4. ¿Cómo interpretar las cantidades negativas en el saldo? <a class="header-anchor" href="#_4-¿como-interpretar-las-cantidades-negativas-en-el-saldo" aria-label="Permalink to &quot;4. ¿Cómo interpretar las cantidades negativas en el saldo?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Un saldo negativo indica:</p><ul><li>📉 Déficit de stock en la cisterna</li><li>⚠️ Posible sobregiro o error en el registro</li><li>🛒 Necesidad de reabastecimiento urgente</li><li>📌 En algunos casos, puede indicar reservas o compromisos de entrega</li></ul><h3 id="_5-¿puedo-generar-el-reporte-para-multiples-articulos-a-la-vez" tabindex="-1">5. ¿Puedo generar el reporte para múltiples artículos a la vez? <a class="header-anchor" href="#_5-¿puedo-generar-el-reporte-para-multiples-articulos-a-la-vez" aria-label="Permalink to &quot;5. ¿Puedo generar el reporte para múltiples artículos a la vez?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> No, este reporte está diseñado para consultar <strong>un artículo específico</strong>. Para múltiples artículos:</p><ul><li>📄 Ejecutar el reporte individualmente para cada artículo</li><li>🧑‍💼 Solicitar un reporte consolidado diferente al administrador del sistema</li></ul><h3 id="_6-¿que-diferencia-hay-entre-cantidad-y-saldo" tabindex="-1">6. ¿Qué diferencia hay entre &quot;Cantidad&quot; y &quot;Saldo&quot;? <a class="header-anchor" href="#_6-¿que-diferencia-hay-entre-cantidad-y-saldo" aria-label="Permalink to &quot;6. ¿Qué diferencia hay entre &quot;Cantidad&quot; y &quot;Saldo&quot;?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong></p><ul><li>🔢 <strong>Cantidad</strong>: Valor del movimiento específico (positivo o negativo)</li><li>💰 <strong>Saldo</strong>: Total acumulado luego de aplicar ese movimiento</li></ul><h3 id="_7-¿por-que-algunos-movimientos-aparecen-en-rojo-verde-o-amarillo" tabindex="-1">7. ¿Por qué algunos movimientos aparecen en rojo, verde o amarillo? <a class="header-anchor" href="#_7-¿por-que-algunos-movimientos-aparecen-en-rojo-verde-o-amarillo" aria-label="Permalink to &quot;7. ¿Por qué algunos movimientos aparecen en rojo, verde o amarillo?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Los colores indican el tipo de movimiento:</p><ul><li>🔴 <strong>Rojo (Carga)</strong>: Movimientos que reducen el stock</li><li>🟢 <strong>Verde (Descarga)</strong>: Movimientos que aumentan el stock</li><li>🟡 <strong>Amarillo (Ajuste)</strong>: Correcciones manuales de inventario</li></ul><h3 id="_8-¿como-puedo-obtener-mas-de-50-registros" tabindex="-1">8. ¿Cómo puedo obtener más de 50 registros? <a class="header-anchor" href="#_8-¿como-puedo-obtener-mas-de-50-registros" aria-label="Permalink to &quot;8. ¿Cómo puedo obtener más de 50 registros?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong></p><ul><li>📆 Si consulta fechas históricas (no el día actual), el límite se elimina automáticamente</li><li>⚙️ Para el día actual, puede solicitar al administrador aumentar el parámetro <code>&quot;limite&quot;</code></li><li>🗓️ Considere usar rangos de fechas más específicos para optimizar la consulta</li></ul><h3 id="_9-¿el-reporte-muestra-informacion-en-tiempo-real" tabindex="-1">9. ¿El reporte muestra información en tiempo real? <a class="header-anchor" href="#_9-¿el-reporte-muestra-informacion-en-tiempo-real" aria-label="Permalink to &quot;9. ¿El reporte muestra información en tiempo real?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Sí, el reporte consulta directamente la base de datos actual. Sin embargo:</p><ul><li>⏱️ Puede haber un pequeño retraso por procesamiento</li><li>🕒 Movimientos muy recientes pueden tardar unos minutos en reflejarse</li><li>🔁 La actualización depende de la frecuencia de sincronización del sistema</li></ul><h3 id="_10-¿que-hago-si-el-reporte-tarda-mucho-en-cargar" tabindex="-1">10. ¿Qué hago si el reporte tarda mucho en cargar? <a class="header-anchor" href="#_10-¿que-hago-si-el-reporte-tarda-mucho-en-cargar" aria-label="Permalink to &quot;10. ¿Qué hago si el reporte tarda mucho en cargar?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Para mejorar el rendimiento:</p><ul><li>📉 Reduzca el rango de fechas consultado</li><li>⚙️ Use el parámetro <code>&quot;limite&quot;</code> para consultas exploratorias</li><li>📆 Evite consultar períodos de más de 3 meses</li><li>🕰️ Consulte durante horarios de menor actividad del sistema</li></ul><h3 id="_11-¿puedo-exportar-los-datos-a-excel" tabindex="-1">11. ¿Puedo exportar los datos a Excel? <a class="header-anchor" href="#_11-¿puedo-exportar-los-datos-a-excel" aria-label="Permalink to &quot;11. ¿Puedo exportar los datos a Excel?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Sí, dependiendo de la configuración del sistema:</p><ul><li>📤 Use la opción de exportación disponible en la interfaz</li><li>🧑‍💼 Solicite al administrador habilitar formatos adicionales (PDF, Excel, CSV)</li><li>📑 También puede guardar el reporte como PDF y convertirlo posteriormente</li></ul><h3 id="_12-¿que-significa-el-numero-que-aparece-en-el-encabezado-junto-al-nombre-de-la-cisterna" tabindex="-1">12. ¿Qué significa el número que aparece en el encabezado junto al nombre de la cisterna? <a class="header-anchor" href="#_12-¿que-significa-el-numero-que-aparece-en-el-encabezado-junto-al-nombre-de-la-cisterna" aria-label="Permalink to &quot;12. ¿Qué significa el número que aparece en el encabezado junto al nombre de la cisterna?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Es el <strong>total disponible actual</strong> del artículo en la cisterna. Este valor:</p><ul><li>🔄 Se calcula en tiempo real</li><li>🛢️ Incluye todos los depósitos de la cisterna</li><li>📅 Es independiente del período consultado en el detalle</li></ul><h3 id="_13-¿por-que-las-fechas-aparecen-en-formato-24-horas" tabindex="-1">13. ¿Por qué las fechas aparecen en formato 24 horas? <a class="header-anchor" href="#_13-¿por-que-las-fechas-aparecen-en-formato-24-horas" aria-label="Permalink to &quot;13. ¿Por qué las fechas aparecen en formato 24 horas?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> El formato 24 horas (<code>HH:MM</code>) ofrece:</p><ul><li>⏱️ Mayor precisión para el control de inventario</li><li>❌ Eliminación de ambigüedad AM/PM</li><li>🌍 Estándar internacional en sistemas empresariales</li><li>🧾 Mejor trazabilidad de movimientos por hora</li></ul><h3 id="_14-¿puedo-filtrar-por-tipo-de-movimiento-especifico" tabindex="-1">14. ¿Puedo filtrar por tipo de movimiento específico? <a class="header-anchor" href="#_14-¿puedo-filtrar-por-tipo-de-movimiento-especifico" aria-label="Permalink to &quot;14. ¿Puedo filtrar por tipo de movimiento específico?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Este reporte <strong>no incluye un filtro por tipo de movimiento</strong>. Para obtener solo un tipo:</p><ul><li>👁️ Analice visualmente los colores en el reporte</li><li>🧑‍💼 Solicite al administrador un reporte personalizado con ese filtro</li><li>🛠️ Use herramientas externas para filtrar los datos exportados</li></ul><h3 id="_15-¿que-debo-hacer-si-encuentro-un-error-en-los-datos" tabindex="-1">15. ¿Qué debo hacer si encuentro un error en los datos? <a class="header-anchor" href="#_15-¿que-debo-hacer-si-encuentro-un-error-en-los-datos" aria-label="Permalink to &quot;15. ¿Qué debo hacer si encuentro un error en los datos?&quot;">​</a></h3><p><strong>🅰️ Respuesta:</strong> Si detecta inconsistencias:</p><ul><li>📝 Documente el problema (fecha, referencia, cantidad, etc.)</li><li>🔍 Verifique en el sistema fuente si el error persiste</li><li>📬 Contacte al administrador del sistema con la información detallada</li><li>❗ No realice correcciones manuales sin autorización</li></ul><h2 id="🆘-soporte-tecnico" tabindex="-1">🆘 Soporte Técnico <a class="header-anchor" href="#🆘-soporte-tecnico" aria-label="Permalink to &quot;🆘 Soporte Técnico&quot;">​</a></h2><h3 id="tips-utiles-para-pedir-ayuda" tabindex="-1">Tips útiles para pedir ayuda <a class="header-anchor" href="#tips-utiles-para-pedir-ayuda" aria-label="Permalink to &quot;Tips útiles para pedir ayuda&quot;">​</a></h3><ul><li>📸 Capturas de pantalla de errores o filtros usados</li><li>📅 Especificar rangos de fecha y tipo de reporte</li><li>📥 Exportar y compartir el Excel si es posible</li></ul><p>📩 <strong>Contacto de soporte</strong>:<br><a href="https://surl.li/kmiuwb" target="_blank" rel="noreferrer"><code>Ingresa al formulario</code></a></p>',102)]))}const f=a(s,[["render",n]]);export{g as __pageData,f as default};
